import 'dart:io';import 'package:camera/camera.dart';import 'package:google_mlkit_text_recognition/google_mlkit_text_recognition.dart';import 'package:permission_handler/permission_handler.dart';class OcrService{static Future<String> scanAndRecognizeText() async{await Permission.camera.request();final cams=await availableCameras();final cam=cams.first;final c=CameraController(cam,ResolutionPreset.high,enableAudio:false);await c.initialize();final pic=await c.takePicture();await c.dispose();final input=InputImage.fromFilePath(pic.path);final tr=TextRecognizer();final rt=await tr.processImage(input);await tr.close();try{File(pic.path).deleteSync();}catch(_){ }return rt.text;}}